import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import ContentHeader from '../../componentes/ContentHeader';
import Footer from '../../componentes/Footer';
import Navbar from '../../componentes/Navbar';
import SidebarContainer from '../../componentes/SidebarContainer';
import APIInvoke from '../../utils/APIInvoke';

const Envios = () => {

    const [envios, setEnvios] = useState([]);

    const cargarEnvios = async () => {
        const response = await APIInvoke.invokeGET(`/api/envios`);
        console.log(response.envios);
        setEnvios(response.envios);
    }

    useEffect(() => {
        cargarEnvios();
    }, [])

    return (
        <div>
            <div className='wrapper'>
                <Navbar></Navbar>
                <SidebarContainer></SidebarContainer>
                <div className="content-wrapper">
                    <ContentHeader
                        titulo={"Envios"}
                        breadCrumb1={"Inicio"}
                        breadCrumb2={"Envios"}
                        ruta1={"/home"}
                    />
                    <section className="content">
                        <div className="card">
                            <div className="card-header">
                                <h3 className="card-title">Title</h3>
                                <div className="card-tools">
                                    <button type="button" className="btn btn-tool" data-card-widget="collapse" title="Collapse">
                                        <i className="fas fa-minus" />
                                    </button>
                                    <button type="button" className="btn btn-tool" data-card-widget="remove" title="Remove">
                                        <i className="fas fa-times" />
                                    </button>
                                </div>
                            </div>
                            <div className="card-body">
                                <table className="table table-bordered">
                                    <thead>
                                        <tr>
                                            <th style={{ width: '10%' }}>Id Transacci√≥n</th>
                                            <th style={{ width: '10%' }}>Id del envio</th>
                                            <th style={{ width: '10%' }}>Id del cliente</th>
                                            <th style={{ width: '10%' }}>Usuario creador</th>
                                            <th style={{ width: '10%' }}>Fecha</th>
                                            <th style={{ width: '10%' }}>Opciones</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            envios.map(
                                                item =>
                                                    <tr key={item._id}>
                                                        <td>{item._id}</td>
                                                        <td>{item.idEnvio}</td>
                                                        <td>{item.idCliente}</td>
                                                        <td>{item.creador}</td>
                                                        <td>{item.creado}</td>
                                                        <td>
                                                            <button className='btn '  ></button>  
                                                        </td>
                                                    </tr>
                                            )
                                        }


                                    </tbody>
                                </table>

                            </div>

                        </div>


                    </section>
                </div>
                <Footer></Footer>
            </div>
        </div>
    );
}

export default Envios;
