import React, {useState, useEffect} from 'react';
import { Link , useNavigate} from 'react-router-dom';
const Login = () => {

    const navigate = useNavigate();

    const [usuario, setUsuario] = useState({
        email: '',
        password:''
    });

    const { email, passwordCliente} = usuario;

    const onChange = (e) => {
        setUsuario({
            ...usuario,
            [e.target.name]: e.target.value
        });
    }

    useEffect( () => {
        document.getElementById("emailCliente").focus();
    },[])

    const onSubmit = (e) => {
        e.preventDefault();
    }
    return (
        <div className="hold-transition login-page">
            <div className="login-box">
                <div className="login-logo">
                    <Link to={"#"}><b>Iniciar</b> Sesión</Link>
                </div>
                {/* /.login-logo */}
                <div className="card">
                    <div className="card-body login-card-body">
                        <p className="login-box-msg">Bienvenido, ingrese sus credenciales.</p>
                        <form onSubmit={onSubmit}>
                            <div className="input-group mb-3">
                                <input type="email" 
                                     className="form-control"
                                     placeholder="Email" 
                                     id="emailCliente"
                                     name="emailCliente"
                                     value={emailCliente}
                                     onChange={onChange}
                                     required
                                />
                                <div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-envelope" />
                                    </div>
                                </div>
                            </div>
                            <div className="input-group mb-3">
                                <input type="password"
                                    className="form-control"
                                    placeholder="Contraseña"
                                    id="passwordCliente"
                                    name="passwordCliente"
                                    value={passwordCliente}
                                     onChange={onChange}
                                     required
                                />
                                <div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-lock" />
                                    </div>
                                </div>
                            </div>
                            <div className="social-auth-links text-center mb-3">
                                <button type='submit' className="btn btn-block btn-primary">
                                    Ingresar
                                </button>
                                <Link to={"/crear-cuenta"} className="btn btn-block btn-danger">
                                    Registrar Cliente
                                </Link>
                                <Link to={"/crear-cuenta-usuario"} className="btn btn-block btn-success">
                                    Registrar Usuario
                                </Link>
                            </div>
                        </form>
                        {/* /.social-auth-links */}
                    </div>
                    {/* /.login-card-body */}
                </div>
            </div>
            {/* /.login-box */}
        </div>

    );
}

export default Login;
